var card = document.getElementById("detail")

    function CargarInfo(id){
        var url = "https://rickandmortyapi.com/api/character/"+id
        
        fetch(url)
        .then((response) => {
                return response.json()
            })
            .then((data) => {
                    console.log(data)
                    card.innerHTML=`
                    <img src="../images/${id}.png" />
                    <div class="card-info">
                      <p>Nome: ${data.name}</p>
                      <p>Estatus: ${data.status}</p>
                      <p>Espêcie: ${data.species}</p>
                      <p>Tipo: ${data.type}</p>
                      <p>Gênero: ${data.gender}</p>
                      <p>Origem: ${data.origin.name}</p>
                       `
                })
            }
